    // app/router.js
    import Ember from 'ember';
    import config from './config/environment';

    const Router = Ember.Router.extend({
      location: config.locationType
    });

    Router.map(function() {
      this.route('foo1', function() {
        this.route('foo2');
      });
    });

    export default Router;
    ```

