      return 'Soviet'; 
    } 
    if (rocket.name.indexOf('CN:') === 0) { 
      return 'Chinese'; 
    } 
    if (rocket.name.indexOf('US:') === 0) { 
      return 'American'; 
    } 

    return 'unknown'; 
  }); 

