class WebTest extends PHPUnit_Framework_TestCase {
    protected $_session;
    protected $_web_driver;

    public function __construct() {
      parent::__construct();
      $_web_driver = new WebDriver();
      $this->_session = $_web_driver->session('firefox');
    }

    public function __destruct() {
        $this->_session->close();
        unset($this->_session);
    }
} 
