    function playerAWin() {
      // reset the ball;
      pingpong.ball.x = 250;
      pingpong.ball.y = 100;

      // update the ball location variables;
      pingpong.ball.directionX = -1;
    }
    function playerBWin() {
      // reset the ball;
      pingpong.ball.x = 150;
      pingpong.ball.y = 100;

      pingpong.ball.directionX = 1;
