      @font-face {
        font-family: "@{family}";
        @file: "../fonts/@{filename}";
        src: url("@{file}.eot");
        src: url("@{file}.eot?#iefix") format("eot"),
             url("@{file}.woff") format("woff"),
             url("@{file}.ttf") format("truetype"),
             url("@{file}.svg#webfont") format("svg");
      }
    }

