    .keyframes (@name, @color0, @color25, @color50, @color100) {
        @-webkit-keyframes @name { 
            0% {background: @color0} 
            25%  {background: @color25;}
            50%  {background: @color50;}
            100% {background: @color100;}
        }

        @keyframes @name { 
            0% {background: @color0} 
            25%  {background: @color25;}
            50%  {background: @color50;}
            100% {background: @color100;}
        }
    }
