App.TabsController = Ember.ArrayController.extend({

  actions: {

    selected: function(selectedTab){

      this.forEach(function(tab){

        var selected = tab.get('name') === selectedTab.get('name');

        tab.set('selected', selected);

      });

    }

  }

});
