app.get('/api/users', function(req, res) { 
    mongo.connect(url, function(err, db) { 
        db.collection('users').find() 
            .toArray(function(err, result) { 
            if (err) { 
                throw err; 
            } 
            console.log(result.length + ' documents retrieved.'); 
            res.json(result); 
            db.close(); 
        }); 
    }); 
}); 

