app.put('/api/users', function(req, res) { 
    mongo.connect(url, function(err, db) { 
        db.collection('users').updateOne( 
            { "id": 1 }, 
            req.body, 
            function(err, result) { 
                if (err) { 
                    throw err; 
                } 
                console.log(result); 
                res.json(result); 
                db.close(); 
            } 
        ); 
    }); 
}); 

